mapper.select:
SELECT nt_number, nt_title, nt_writer, nt_content, TO_CHAR(nt_regdate, 'YYYY-MM-DD') AS nt_regdate, nt_readcnt FROM (SELECT list.*, ROWNUM AS RNUM FROM (
ORDER BY nt_regdate
ASC
DESC
) list )
RNUM BETWEEN #{start_row} AND #{end_row}

SELECT NVL(count(1), 0) FROM
(SELECT list.*, ROWNUM AS RNUM
FROM (

ORDER BY nt_number desc
) list
)

mapper.update:
update notice_tbl
set
nt_title = #{nt_title}
, nt_content = #{nt_content}
, nt_regdate =
sysdate
where nt_number = #{nt_number}
