mapper.select:
SELECT pt_number, pt_name, pt_type, pt_price, pt_explain, TO_CHAR(pt_regdate, 'YYYY-MM-DD') AS pt_regdate, pt_status, substr(pt_thumb,21) as pt_thumb, substr(pt_image1,22) as pt_image1,substr(pt_image2,22) as pt_image2 ,substr(pt_image3,22) as pt_image3 FROM (SELECT list.*, ROWNUM AS RNUM FROM(
ORDER BY pt_type
ASC
DESC
) list)
rnum BETWEEN #{start_row} AND #{end_row}

mapper.insert:

	select product_seq.nextval from dual

INSERT INTO product_tbl(
	pt_number,pt_name,pt_type,pt_price,pt_explain,pt_regdate,pt_status,pt_thumb

		,pt_image1


		,pt_image2


		,pt_image3

	)
VALUES(
#{pt_number},
#{pt_name},
#{pt_type},
#{pt_price},
#{pt_explain},
sysdate,
#{pt_status},
#{pt_thumb}

,#{pt_image1}


,#{pt_image2}


,#{pt_image3}

)

mapper.update:

UPDATE product_tbl
SET pt_type = #{pt_type}
, pt_name = #{pt_name}

,pt_thumb = #{pt_thumb}


,pt_image1 = #{pt_image1}


,pt_image2 = #{pt_image2}


,pt_image3 = #{pt_image3}

,pt_explain = #{pt_explain}
,pt_price = #{pt_price}
,pt_status = #{pt_status}
WHERE pt_number = #{pt_number}
