mapper.select:
SELECT qt_number, mt_id, qt_title, qt_content, qt_status, qt_pw, TO_CHAR(qt_regdate, 'YYYY-MM-DD') AS qt_regdate, qt_writer, qt_file, qt_root, qt_step, qt_level FROM (SELECT list.*, ROWNUM AS RNUM FROM (
order by qt_level ) list )
RNUM BETWEEN #{start_row} AND #{end_row}

mapper.delete:
DELETE FROM
QUESTION_TBL

	WHERE QT_NUMBER = #{qt_number}

WHERE QT_NUMBER = #{qt_number}
OR QT_ROOT = #{qt_number}

mapper.update:
update question_tbl
set qt_status = '답변완료'
where qt_number = #{qt_root}

mapper.select:
SELECT NVL(count(1), 0) FROM
( SELECT list.*, ROWNUM AS RNUM
FROM (

ORDER BY qt_level desc
) list
)

mapper.update:
update Question_tbl
set
qt_content = #{qt_content}

