mapper.select:
SELECT nvl(count(1),0) FROM(SELECT r.rent_number,re.rest_bnumber, re.rest_bname, re.rest_name,re.rest_number, re.rest_phone,re.rest_total,re.rest_bnumber,re.rest_count,r.rent_status,r.rent_overtime ,to_char(r.rent_actrent,'YYYY-MM-DD HH24:MI') as rent_actrent , to_char(r.RENT_EXPRETRUN,'YYYY-MM-DD HH24:MI') as RENT_EXPRETRUN ,p.pt_name, m.mt_id, r.rent_addcost ,to_char(r.rent_actretrun,'YYYY-MM-DD HH24:MI') as rent_actretrun FROM(SELECT list.*,ROWNUM as RNUM FROM(SELECT * FROM rental_tbl
ORDER BY rent_status
ORDER BY RENT_EXPRETRUN
ORDER BY RENT_ACTRETRUN
ASC
DESC
)list) r ,product_tbl p ,member_tbl m ,reservation_tbl re where r.mt_id = m.mt_id and r.pt_number = p.pt_number and r.rest_number = re.rest_number
and rnum BETWEEN #{start_row} AND #{end_row}
)

mapper.update:
UPDATE rental_tbl
SET rent_status = '반납완료', rent_actretrun = sysdate , rent_overtime = #{rent_overtime} , rent_addcost = #{rent_addcost}
WHERE rent_number = #{rent_number}
